<script>
function openDiv(evt,divName) {
  var i, tabcontent,tablinks;
  tabcontent = document.getElementsByClassName("tabcontent");
  for (i = 0; i < tabcontent.length; i++) {
    tabcontent[i].style.display = "none";
  }
  tablinks = document.getElementsByClassName("tablinks");
  for (i = 0; i < tablinks.length; i++) {
    tablinks[i].className = tablinks[i].className.replace(" active", "");
  }
  document.getElementById(divName).style.display = "block";
  evt.currentTarget.className += " active";
}

function editform()
{
  document.getElementById("save").style.display="inline";	
  document.getElementById("cancel").style.display="inline";
  document.getElementById("edit").style.display="none";
  document.getElementById("dob").readOnly=false;
  document.getElementById("bloodgroup").readOnly=false;
  document.getElementById("mob").readOnly=false;  
  document.getElementById("email").readOnly=false;
  document.getElementById("fmob").readOnly=false;  
  document.getElementById("femail").readOnly=false;
  document.getElementById("mmob").readOnly=false;  
  document.getElementById("memail").readOnly=false;
  document.getElementById("dob").style.backgroundColor="white";
  document.getElementById("bloodgroup").style.backgroundColor="white";
  document.getElementById("mob").style.backgroundColor="white";  
  document.getElementById("email").style.backgroundColor="white";
  document.getElementById("fmob").style.backgroundColor="white";  
  document.getElementById("femail").style.backgroundColor="white";
  document.getElementById("mmob").style.backgroundColor="white";  
  document.getElementById("memail").style.backgroundColor="white";
}
